/*==================================================
AllCampusesView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display all campuses.
================================================== */
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import Button from "@material-ui/core/Button";

const AllCampusesView = (props) => {
  // If there is no campus, display a message.
  if (!props.allCampuses.length) {
    return (
      <div style={{ paddingTop: "1em" }}>
        <p>There are no campuses.</p>
        <Link to={`newcampus`}>
          <Button style={{ color: "white", backgroundColor: "grey" }}>
            Add New Campus
          </Button>
        </Link>
      </div>
    );
  }

  return (
    <div>
      <h1>All Campuses</h1>

      {props.allCampuses.map((campus) => (
        <div key={campus.id}>
          <Link to={`/campus/${campus.id}`}>
            <h2>{campus.name}</h2>
          </Link>
          <img
            src={
              campus.imageurl ||
              "https://www.zillowstatic.com/bedrock/app/uploads/sites/26/shutterstock_262043447-dedc70.jpg"
            }
            style={{
              maxWidth: "100%",
              maxHeight: "100px",
              borderRadius: "8px", 
              boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)", 
              transition: "transform 0.3s ease",
            }}
            onMouseOver={(e) =>
              (e.currentTarget.style.transform = "scale(1.05)")
            } // Scales up the image slightly on hover
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")} // Returns to normal scale when not hovering
          />

          <h4>
            Campus ID: {campus.id} | Total Students Enrolled:{" "}
            {campus.students.length}
          </h4>
          <hr />
        </div>
      ))}
      <br />
      <Link to={`/newcampus`}>
        <Button style={{ color: "white", backgroundColor: "rgb(85, 26, 139)" }}>
          Add New Campus
        </Button>
      </Link>
      <br />
      <br />
    </div>
  );
};

// Validate data type of the props passed to component.
AllCampusesView.propTypes = {
  allCampuses: PropTypes.array.isRequired,
};

export default AllCampusesView;
